#include <iostream>

char bigAssNumber[]=
"";

static inline int charToInt(char c){return (int)c -48;}

int main()
{
    // Boring Clock Stuff Begin //
    clock_t begin, end;
    double elapsed;
    
    begin = clock();
    // End Boring Clock Stuff  //
    
    int limit = (sizeof(bigAssNumber) / sizeof(char)) - 1; // Get size of input number
    long final = 1;
    long current=charToInt(bigAssNumber[0]);
    
    printf("test: %ld",current);
    
    //Initialize first 13 numbers
    for (int i = 1; i<=13; ++i)
    {
        current *= charToInt(bigAssNumber[i]);
    }
    
    printf("test: %ld",current);
    for (int i = 13; i < limit; ++i)
    {
            current *= charToInt(bigAssNumber[i])/charToInt(bigAssNumber[i-13]);
            if (current > final)
            {
                final = current;
            }
    }
    
    end = clock();
    elapsed = (double)(end-begin) / CLOCKS_PER_SEC;
    
    printf("Maximum product of any 13 digits is: %ld \n",final);
    printf("Elapsed time is: %f seconds \n",elapsed);
    return 0;
    
}